    public static final Pattern PHONENUMBER_VALID_ADDRESS_REGEX = Pattern.compile(
            "([\\+(]?(\\d){2,}[)]?[- \\.]?(\\d){2,}[- \\.]?(\\d){2,}[- \\.]?(\\d){2,}[- \\.]?(\\d){2,})|([\\+(]?(\\d){2,}[)]?[- \\.]?(\\d){2,}[- \\.]?(\\d){2,}[- \\.]?(\\d){2,})|([\\+(]?(\\d){2,}[)]?[- \\.]?(\\d){2,}[- \\.]?(\\d){2,})",
            Pattern.CASE_INSENSITIVE);

    public String getContentFromFile(String destFilePath) throws IOException, SAXException, TikaException {
        File file = new File(destFilePath);
        Parser parser = new AutoDetectParser();
        BodyContentHandler handler = new BodyContentHandler();
        Metadata metadata = new Metadata();
        FileInputStream inputstream = new FileInputStream(file);
        ParseContext context = new ParseContext();
        parser.parse(inputstream, handler, metadata, context);
        return handler.toString();
    }

    public String getEmailId(String emailStr) {
        Matcher matcher = VALID_EMAIL_ADDRESS_REGEX.matcher(emailStr);
        if (matcher.find()) {
            return matcher.group();
        }
        return null;
    }

    public String getPhoneNum(String phonenumber) {
        Matcher matcher = PHONENUMBER_VALID_ADDRESS_REGEX.matcher(phonenumber);
        if (matcher.find()) {
            return matcher.group();
        }
        return null;
    }
